mapper.select:
SELECT iuser, upw, nm
FROM t_user
WHERE uid = #{uid}

SELECT upw, nm, phone_number, email
FROM t_user
WHERE iuser = #{iuser}

mapper.update:
UPDATE t_user
SET unregister_fl = 1
WHERE iuser = #{iuser}

UPDATE t_user SET
upw = #{upw},
nm = #{nm}, phone_number = #{phoneNumber}, email = #{email} WHERE iuser = #{iuser}
